class TypeWriter{constructor(txtElement, words, wait=3000){this.txtElement=txtElement; this.words=words; this.txt=''; this.wordIndex=0; this.wait=parseInt(wait, 10); this.type(); this.isDeleting=false;}type(){const current=this.wordIndex % this.words.length; const fullTxt=this.words[current]; if (this.isDeleting){this.txt=fullTxt.substring(0, this.txt.length - 1);}else{this.txt=fullTxt.substring(0, this.txt.length + 1);}this.txtElement.innerHTML=`<span class="txt">${this.txt}</span>`; let typeSpeed=300; if (this.isDeleting){typeSpeed /=2;}if (!this.isDeleting && this.txt===fullTxt){typeSpeed=this.wait; this.isDeleting=true;}else if (this.isDeleting && this.txt===''){this.isDeleting=false; this.wordIndex++; typeSpeed=500;}setTimeout(()=> this.type(), typeSpeed);}}document.addEventListener('DOMContentLoaded', init);function init(){const txtElement=document.querySelector('.txt-type'); const words=JSON.parse(txtElement.getAttribute('data-words')); const wait=txtElement.getAttribute('data-wait'); new TypeWriter(txtElement, words, wait);}const nav=document.querySelector('#navbar');const navBtn=document.querySelector('.nav-btn');const navBtnLines=document.querySelectorAll('.btn-line');const links=document.querySelector('#links');window.addEventListener('scroll', navScroll);navBtn.addEventListener('click', navOpen);function navScroll(){if (pageYOffset > 500){nav.classList.add('nav-active');}else{nav.classList.remove('nav-active');}}function navOpen(){links.classList.toggle('ul-open'); navBtn.classList.toggle('nav-btn-open'); navBtnLines[0].classList.toggle('nav-btn-line-one'); navBtnLines[1].classList.toggle('nav-btn-line-two'); navBtnLines[2].classList.toggle('nav-btn-line-three');}const slider=document.querySelector('.slider-body');const image=document.querySelector('.main-image');const arrowLeft=document.querySelector('.arrow-left');const arrowRight=document.querySelector('.arrow-right');const closeBtn=document.querySelector('.close-btn');const thumbnail=document.getElementsByClassName('thumbnail');const thumbnails=document.querySelector('.thumbnails');for (let i=0; i < thumbnail.length; i++){thumbnail[i].addEventListener('click', openSlider);}thumbnails.style.width=18.3 * thumbnail.length + 'em';closeBtn.addEventListener('click', closeSlider);function openSlider(e){let id=0; for (let i=0; i < thumbnail.length; i++){thumbnail[i].setAttribute('data-id-type', id++);}slider.style.display='block'; image.src=e.target.src; let arr=thumbnail.length; arr=e.target.getAttribute("data-id-type"); arrowLeft.addEventListener('click', slideLeft); arrowRight.addEventListener('click', slideRight); function slideLeft(){arr--; if (arr < 0){arr=thumbnail.length - 1;}image.src=thumbnail[arr].src;}function slideRight(){arr++; if (arr > thumbnail.length - 1){arr=0;}image.src=thumbnail[arr].src;}}function closeSlider(e){slider.style.display='none'; if (e.target==image.parentElement.parentElement){slider.style.display='none';}}const btnFive=document.querySelectorAll('.card-five-btn-circle');const btnFiveLeft=document.querySelectorAll('.five-btn-left');const btnFiveRight=document.querySelectorAll('.five-btn-right');const fiveOverlay=document.querySelectorAll('.card-five-top-overlay');const fiveInfo=document.querySelectorAll('.five-info');const fiveIcons=document.querySelectorAll('.five-social-media');const cardFive=document.querySelectorAll('.card-five');for (let i=0; i < cardFive.length; i++){btnFive[i].innerHTML='+'; btnFive[i].addEventListener('click', slideFiveOpen); function slideFiveOpen(){fiveOverlay[i].classList.toggle('five-overlay-slide'); btnFiveRight[i].classList.toggle('five-btn-right-slide'); btnFiveLeft[i].classList.toggle('five-btn-left-slide'); fiveInfo[i].classList.toggle('five-info-slide'); if (fiveOverlay[i].classList.contains('five-overlay-slide')){btnFive[i].innerHTML='-'; btnFive[i].style.top='2.7em'; fiveIcons[i].style.display='none';}else{btnFive[i].innerHTML='+'; btnFive[i].style.top='1.4em'; fiveIcons[i].style.display='block';}}}window.onload=function (){document.addEventListener("contextmenu", function (e){e.preventDefault();}, false); document.addEventListener("keydown", function (e){if (e.ctrlKey && e.shiftKey && e.keyCode==73){disabledEvent(e);}if (e.ctrlKey && e.shiftKey && e.keyCode==74){disabledEvent(e);}if (e.keyCode==83 && (navigator.platform.match("Mac") ? e.metaKey : e.ctrlKey)){disabledEvent(e);}if (e.ctrlKey && e.keyCode==85){disabledEvent(e);}if (event.keyCode==123){disabledEvent(e);}}, false); function disabledEvent(e){if (e.stopPropagation){e.stopPropagation();}else if (window.event){window.event.cancelBubble=true;}e.preventDefault(); return false;}}; 